import{_ as e,o as t,c as a,O as o}from"./chunks/framework.7d066bc9.js";const s="/assets/W020230404324919860627.d4a2beb6.png",A=JSON.parse('{"title":"Aβ寡聚体与神经元作用机制研究获进展","description":"","frontmatter":{"title":"Aβ寡聚体与神经元作用机制研究获进展","hidden":false,"tag":["力学","生物学","其他"],"sticky":100,"top":1},"headers":[],"relativePath":"tag_news/202304_1.md","filePath":"tag_news/202304_1.md","lastUpdated":null}'),n={name:"tag_news/202304_1.md"},r=o('<h2 id="aβ寡聚体与神经元作用机制研究获进展" tabindex="-1">Aβ寡聚体与神经元作用机制研究获进展 <a class="header-anchor" href="#aβ寡聚体与神经元作用机制研究获进展" aria-label="Permalink to &quot;Aβ寡聚体与神经元作用机制研究获进展&quot;">​</a></h2><blockquote><p><strong>标签:</strong> [力学] [生物学] [其他]<br><strong>高频词:</strong> 神经元, , 力学, 研究, β</p></blockquote><p>中国科学院重庆绿色智能研究院与重庆大学、中科院上海高等研究院和上海交通大学等合作，在Aβ42寡聚体与神经元作用机制研究中取得进展。Aβ42寡聚体可以引起神经元的功能缺失，从力学生物学的角度研究其作用机制对于剖析神经元功能缺失相关的神经退行性疾病有重要意义。然而，细胞（神经元）结构复杂，如何用力学参数综合表征神经元以及如何将力学参数与神经元生物学特性关联颇具挑战性。</p><p>研究团队发展了基于细胞异质结构和原子力显微镜（AFM）荷载-卸载全力谱的纳米力学分析模型（HLUN）。研究显示，通过HLUN可以获取四个表观力学参数（杨氏模量、细胞弹性常数、归一化迟滞因子和黏附功）来综合表征Aβ42寡聚体处理前后海马神经元的力学特性。这四个力学参数与神经元高度、肌动蛋白丝强度和钙离子浓度变化密切关联。该工作建立了单神经元AFM纳米力学分析方法，并将Aβ42寡聚体诱导的神经元生物学变化与力学参数变化有效关联，为探讨单细胞提供了有效的力学生物学研究途径。</p><p>相关研究成果以<em>Nanomechanical Profiling of Aβ42 Oligomer-Induced Biological Changes in Single Hippocampus Neurons</em>为题，发表在<em>ACS Nano</em>上。研究工作得到国家重点研发计划和国家自然科学基金的支持。</p><p><a href="https://pubs.acs.org/doi/full/10.1021/acsnano.2c10861" target="_blank" rel="noreferrer">论文链接</a></p><p><img src="'+s+'" alt=""></p><p>Aβ寡聚体与神经元作用机制研究获进展</p><p></p>',9),_=[r];function p(c,i,l,d,m,g){return t(),a("div",null,_)}const f=e(n,[["render",p]]);export{A as __pageData,f as default};
